import React, { useEffect, useState } from "react";
import { demouser } from "../Assets/index";
import { toast } from 'react-toastify';
import axios from 'axios';

const Navbar = ({ pagename }) => {
  const [data, setData] = useState({ msg: "Fetching data...", luckyNumber: null });

  const fetchLuckyNumber = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/v1/dashboard");
      console.log("API Response:", response.data); // ✅ Debugging
      setData({ 
        msg: response.data?.msg || "No message received", 
        luckyNumber: response.data?.secret || "No number available" 
      });
    } catch (error) {
      toast.error(error.message);
    }
  };

  useEffect(() => {
    fetchLuckyNumber();
  }, []);

  return (
    <nav className="bg-white flex items-center justify-between h-20 px-8 shadow-sm">
      <div className="flex items-center">
        <h1 className="text-lg font-semibold text-gray-500">{pagename}</h1>
      </div>
      <div className="flex items-center">
        <div className="rounded-full h-10 w-10 bg-gray-300 flex items-center justify-center mr-4">
          <img src={demouser} alt="avatar" className="rounded-full h-8 w-8" />
        </div>
        <div className="flex flex-col text-sm">
          <span className="font-medium">{data.msg || "Loading..."}</span>
          <span>Super Admin</span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
